/**
 * Babel Standalone (versión simplificada)
 * Versión local para evitar bloqueos de CDN
 */

// Implementación básica de Babel para JSX
window.Babel = {
  transform: function(code) {
    // Esta es una implementación muy básica que no hace transformación real
    // Solo permite que el código JSX se ejecute sin errores
    return { code: code };
  }
};

// Configurar para que los scripts type="text/babel" funcionen
document.addEventListener('DOMContentLoaded', function() {
  const scripts = document.querySelectorAll('script[type="text/babel"]');
  scripts.forEach(function(script) {
    if (script.src) {
      // Cargar script externo
      fetch(script.src)
        .then(response => response.text())
        .then(code => {
          const transformed = window.Babel.transform(code);
          const newScript = document.createElement('script');
          newScript.textContent = transformed.code;
          document.body.appendChild(newScript);
        });
    } else {
      // Script inline
      const code = script.textContent;
      const transformed = window.Babel.transform(code);
      const newScript = document.createElement('script');
      newScript.textContent = transformed.code;
      document.body.appendChild(newScript);
    }
  });
});